int position = 0;
	
void horizon_draw(Display *display, int move, uint32_t mountainColor, uint32_t skyColor, uint32_t grassColor) {
	int x,y, d_x;
	position += move;
	if(position >= 128)
		position = position % 128;
	else if (position <= -128)
		position += 128; 
	for(x = 0; x < 128; x++)
		 for(y = 0; y < 32; y++) {
				d_x = x + position;
				display->back_buffer[(d_x > 0)? (x + position)%128 : 128 + d_x][y] = (!horizon_bin[y*128+x])?skyColor:mountainColor;
		 }
	//Draw the grass
	for(x = 0; x < 128; x++)
		 for(y =32; y < 96; y++)
		 display->back_buffer[x][y] = grassColor;
}